mapper.select:
SELECT COUNT(*) FROM POSTS
CATEGORY = #{category}
AND TITLE LIKE '%' || #{keyword} || '%'

SELECT * FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT P.*, U.NICKNAME,
		       (SELECT COUNT(*) FROM COMMENTS C WHERE C.POST_ID = P.POST_ID) AS COMMENT_COUNT
		FROM POSTS P
		JOIN USERS U ON P.USER_ID = U.USER_ID
		
			
				P.CATEGORY = #{category}
			
			
				AND
				P.TITLE LIKE '%' || #{keyword} || '%'
			
		
		ORDER BY P.CREATED_AT DESC
	) A
	WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE RN > #{offset}

SELECT * FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT P.POST_ID, P.CATEGORY, P.TITLE, P.CREATED_AT, U.NICKNAME
		FROM POSTS P
		JOIN USERS U ON P.USER_ID = U.USER_ID
		WHERE P.USER_ID = #{userId}
		ORDER BY P.CREATED_AT DESC
	) A
	WHERE ROWNUM <= (#{offset} + #{limit})
)
WHERE RN > #{offset}

SELECT * FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT C.COMMENT_ID, C.POST_ID, C.C_CONTENT, C.CREATED_AT, P.TITLE, U.NICKNAME
		FROM COMMENTS C
		JOIN POSTS P ON C.POST_ID = P.POST_ID
		JOIN USERS U ON C.USER_ID = U.USER_ID
		WHERE C.USER_ID = #{userId}
		ORDER BY C.CREATED_AT DESC
	) A
	WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE RN > #{offset}

mapper.delete:
DELETE FROM SCRAPS
WHERE
USER_ID = #{userId} AND POST_ID = #{postId}

mapper.select:
SELECT COUNT(*) FROM SCRAPS
WHERE USER_ID = #{userId} AND
POST_ID = #{postId}

SELECT * FROM (
SELECT ROWNUM AS RN, A.* FROM (
SELECT P.POST_ID, P.CATEGORY, P.TITLE, P.CREATED_AT, U.NICKNAME,
S.SCRAP_DATE
FROM SCRAPS S
JOIN POSTS P ON S.POST_ID = P.POST_ID
JOIN USERS U ON P.USER_ID = U.USER_ID
WHERE S.USER_ID = #{userId}
ORDER BY S.SCRAP_DATE DESC
) A
WHERE ROWNUM <= (#{offset} + #{limit})
)
WHERE RN > #{offset}
